import React, { createContext, useEffect, useState } from "react";
import axios from "axios";

export const WeatherContext = createContext(null);

const WeatherContextProvider = (props) => {
    const [city, setCity] = useState([]);
    const [country, setCountry] = useState([])
    const [weatherData, setWeatherData] = useState([])
    const APIKEY = process.env.APIKEY

    useEffect(() => {
        async function fetchData() {
            try {
                // const res = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${APIKEY}`);
                const res = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=bronx&appid=eb5bf5cf252dcc79c75fe1ddbad6ceb3`);
                console.log(res.data)
                setWeatherData(res.data)
            } catch (err) {
                console.log("Failed to get data: ", err.message)
            }
        }
        fetchData();
    }, [])

    const contextValue = {weatherData}

    return (
        <WeatherContext.Provider value={contextValue}>
            {props.children}
        </WeatherContext.Provider>
    )
}

export default WeatherContextProvider
